import React, { useState, useContext } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

// Criando o contexto para armazenar os estados compartilhados
const RegisterContext = React.createContext();

// Provedor do contexto
const RegisterProvider = ({ children }) => {
    const [firstname, setfirstname] = useState('');
    const [lastname, setlastname] = useState('');
    const [username, setusername] = useState('');
    const [email, setemail] = useState('');
    const [password, setpassword] = useState('');

    const values = {
        firstname,
        setfirstname,
        lastname,
        setlastname,
        username,
        setusername,
        email,
        setemail,
        password,
        setpassword,
    };

    return (
        <RegisterContext.Provider value={values}>
            {children}
        </RegisterContext.Provider>
    );
};

// Hook personalizado para utilizar o contexto
const useRegisterContext = () => {
    return useContext(RegisterContext);
};

function Register() {
    const {
        firstname,
        setfirstname,
        lastname,
        setlastname,
        username,
        setusername,
        email,
        setemail,
        password,
        setpassword,
    } = useRegisterContext();

    const handleSubmit = async (e) => {
        e.preventDefault();

        const data = {
            firstname,
            lastname,
            username,
            email,
            password,
        };

        const response = await axios.post('http://localhost:9000/register', data);

        if (response.status === 201) {
            console.log('Usuário registrado com sucesso!');
        } else {
            console.log('Erro ao registrar usuário');
        }
    };

    return (
        <div className="register-container">
            <h1>Registro</h1>
            <form action='POST'>
                <input
                    type="text"
                    name="firstname"
                    placeholder="Primeiro nome"
                    value={firstname}
                    onChange={(e) => { setfirstname(e.target.value) }}
                />
                <input
                    type="text"
                    name="lastname"
                    placeholder="Sobrenome"
                    value={lastname}
                    onChange={(e) => { setlastname(e.target.value) }}
                />
                <input
                    type="text"
                    name="username"
                    placeholder="Nome de usuário"
                    value={username}
                    onChange={(e) => { setusername(e.target.value) }}
                />
                <input
                    type="email"
                    name="email"
                    placeholder="E-mail"
                    value={email}
                    onChange={(e) => { setemail(e.target.value) }}
                />
                <input
                    type="password"
                    name="password"
                    placeholder="Senha"
                    value={password}
                    onChange={(e) => { setpassword(e.target.value) }}
                />
                <button onClick={handleSubmit}>Registrar</button>
            </form>
            <p>
                Já possui uma conta? <Link to="/login">Faça login aqui</Link>
            </p>
        </div>
    );
};

export { RegisterProvider, useRegisterContext };
export default Register;
