import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './Login.css';

const Login = () => {
  const [formData, setFormData] = useState({
    username: '',
    password: '',
  });
  const [errorMessage, setErrorMessage] = useState('');

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Validação simples para evitar envio de dados vazios
    if (!formData.username || !formData.password) {
      setErrorMessage('Preencha todos os campos!');
      return;
    }

    // Lógica para enviar os dados do formulário para o backend
    const response = await fetch('/api/login', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(formData),
    });

    // Tratamento da resposta da API
    if (response.ok) {
      // Redirecionar para a página Home
      window.location.href = '/home';
    } else {
      // Exibir mensagem de erro para o usuário
      const errorData = await response.json();
      setErrorMessage(errorData.message || 'Usuário ou senha incorretos!');
    }
  };

  return (
    <div className="login-container">
      <h1>Login</h1>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="username"
          placeholder="Nome de usuário"
          value={formData.username}
          onChange={handleChange}
        />
        <input
          type="password"
          name="password"
          placeholder="Senha"
          value={formData.password}
          onChange={handleChange}
        />
        <button type="submit">Entrar</button>
      </form>
      <p>
        <Link to="/recuperar-senha">Esqueceu a senha? Redefina aqui!</Link>
      </p>
      <p>
        Ainda não tem uma conta? <Link to="/register">Faça o cadastro aqui!</Link>
      </p>
    </div>
  );
};

export default Login;
