import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './Register.css';
import axios from 'axios';

const Register = () => {
    const [formData, setFormData] = useState({
        firstName: '',
        lastName: '',
        username: '',
        email: '',
        password: '',
    });

    const handleChange = (e) => {
        setFormData({
            ...formData,
            [e.target.firstname]: e.target.value,
            [e.target.lastname]: e.target.value,
            [e.target.username]: e.target.value,
            [e.target.email]: e.target.value,
            [e.target.password]: e.target.value,
        });
    };

    const registerUser = async (firstName, lastName, username, email, password) => {
        try {
            const response = await axios.post('/server/app', {
                firstName,
                lastName,
                username,
                email,
                password,
            });
            return response.status === 201;
        } catch (error) {
            console.error(error);
            return false;
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        const { firstName, lastName, username, email, password } = formData;

        const userRegisteredSuccessfully = await registerUser(firstName, lastName, username, email, password);

        if (userRegisteredSuccessfully) {
            // Redirecionar para a tela de login
            window.location.href = '/login';
        } else {
            alert('Erro ao registrar usuário!');
        }
    };

    return (
        <div className="register-container">
            <h1>Registro</h1>
            <form onSubmit={handleSubmit}>
                <input
                    type="text"
                    name="firstname"
                    placeholder="Primeiro nome"
                    value={formData.firstname}
                    onChange={handleChange}
                />
                <input
                    type="text"
                    name="lastname"
                    placeholder="Sobrenome"
                    value={formData.lastname}
                    onChange={handleChange}
                />
                <input
                    type="text"
                    name="username"
                    placeholder="Nome de usuário"
                    value={formData.username}
                    onChange={handleChange}
                />
                <input
                    type="email"
                    name="email"
                    placeholder="E-mail"
                    value={formData.email}
                    onChange={handleChange}
                />
                <input
                    type="password"
                    name="password"
                    placeholder="Senha"
                    value={formData.password}
                    onChange={handleChange}
                />
                <button type="submit">Registrar</button>
            </form>
            <p>
                Já possui uma conta? <Link to="/login">Faça login aqui</Link>
            </p>
        </div>
    );
};

export default Register;
