import React, { createContext, useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

// Crie o contexto para os dados de registro
const RegisterContext = createContext();

// Crie um Provider para fornecer o estado e as funções de atualização
function RegisterProvider({ children }) {
  const [values, setValues] = useState({
    firstname: '',
    lastname: '',
    username: '',
    email: '',
    password: '',
  });

  const handleChange = (e) => {
    setValues({ ...values, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const response = await axios.post('http://localhost:9000/register', values);

    if (response.status === 201) {
      console.log('Usuário registrado com sucesso!');
    } else {
      console.log('Erro ao registrar usuário');
    }
  };

  return (
    <RegisterContext.Provider value={{ values, handleChange, handleSubmit }}>
      {children}
    </RegisterContext.Provider>
  );
}

// Componente Registro que consome o contexto
function Register() {
  const { values, handleChange, handleSubmit } = useContext(RegisterContext);

  return (
    <div className="register-container">
      <h1>Registro</h1>
      <form action="POST">
        <input
          type="text"
          name="firstname"
          placeholder="Primeiro nome"
          value={values.firstname}
          onChange={handleChange}
        />
        {/* ... outros campos de entrada */}
        <button onClick={handleSubmit}>Registrar</button>
      </form>
      <p>
        Já possui uma conta? <Link to="/login">Faça login aqui</Link>
      </p>
    </div>
  );
}

export default RegisterProvider;
